import React, { Component } from "react";
import loader from "./images/loader.svg";
const Header = () => (
  <div className="header grid">
    <h1 className="title">Jiffy</h1>
  </div>
);
const UserHint = ({ loading, hintText }) => (
  <div className="user-hint">
    {loading ? <img className="block mx-auto" src="loader" /> : hintText}
  </div>
);

const randomChoice = arr => {
  const randIndex = Math.floor(Math.random() * arr.length);
  return arr[randIndex];
};

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      searchTerm: "",
      hintText: "",
      gif: null
    };
  }

  searchGiphy = async searchTerm => {
    try {
      const response = await fetch(
        `https://api.giphy.com/v1/gifs/search?api_key=BNBQdaOJdcjRkyZ8t4aavRBRM4zES6qX&q=${searchTerm}&limit=25&offset=0&rating=PG-13&lang=en`
      );
      const { data } = await response.json();

      const randomGif = randomChoice(data);
      console.log({ randomGif });

      this.setState((prevState, props) => ({
        ...prevState,
        gif: data[0]
      }));
    } catch {}
  };

  handleChange = event => {
    const { value } = event.target;
    this.setState((prevState, props) => ({
      ...prevState,
      searchTerm: value,
      hintText: value.length > 2 ? `hit enter to search ${value}` : ""
    }));
  };

  handleKeyPress = event => {
    const { value } = event.target;

    if (value.length > 2 && event.key === "Enter") {
      this.searchGiphy(value);
    }

    console.log(event.key);
  };
  render() {
    const { searchTerm, gif } = this.state;
    return (
      <div className="page">
        <Header />
        <div className="search grid">
          {gif && (
            <video
              className="grid-item video"
              autoPlay
              loop
              src={gif.images.original.mp4}
            />
          )}

          <input
            className="input grid-item"
            placeholder="type something"
            onChange={this.handleChange}
            onKeyPress={this.handleKeyPress}
            value={searchTerm}
          />
        </div>
        <UserHint {...this.state} />
      </div>
    );
  }
}

export default App;
