  <div className="header grid">
    <h1 className="title">Jiffy</h1>
  </div>
);

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      searchTerm: ""
    };
  }

  handleChange = event => {
    const { value } = event.target;
